import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

function UploadPage() {
  const [inputText, setInputText] = useState('');
  const [statusMessage, setStatusMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate(); // For navigating to the results page

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setLoading(true);
    setStatusMessage('Processing...');

    // Simulate a "request" delay with setTimeout
    setTimeout(() => {
      // Simulate generated HTML, CSS, and JS
      const generatedData = {
        html: `<div>Your generated HTML: <strong>${inputText}</strong></div>`,
        css: `div { color: blue; font-size: 16px; }`,
        js: `document.querySelector('div').onclick = function() { alert('Clicked!'); }`,
      };

      // Navigate to the results page and pass the generated data
      navigate('/results', { state: { generatedData } });

      setLoading(false);
    }, 2000); // Simulate 2-second delay
  };

  return (
    <div className="container">
      <header>
        <img src="/logo-no-background.png" alt="Logo" className="logo-top-left" />
      </header>

      <main>
        <form onSubmit={handleSubmit}>
          <div className="input-area">
            <textarea
              value={inputText}
              onChange={handleInputChange}
              placeholder="Enter your prompt or text for the model"
              className="text-input"
              rows="5"
            />
            <button type="submit" className="submit-button" disabled={loading}>
              {loading ? 'Processing...' : 'Generate Code'}
            </button>
          </div>
        </form>

        {/* Display status message */}
        {statusMessage && <div className="status-message">{statusMessage}</div>}
      </main>
    </div>
  );
}

export default UploadPage;
