import React, { useState, useEffect } from 'react';
import Prism from 'prismjs';
import 'prismjs/themes/prism-okaidia.css'; // Optional, choose a theme you like


function ResultsPage() {
  const { state } = useLocation(); // Access the passed generatedData from the previous page
  const [selectedCodeType, setSelectedCodeType] = useState('html'); // Default view: HTML
  const { generatedData } = state;

  const handleToggle = (codeType) => {
    setSelectedCodeType(codeType);
  };

  return (
    <div className="container">
      <header>
        <img src="/logo-no-background.png" alt="Logo" className="logo-top-left" />
      </header>

      <main>
        <div className="toggle-buttons">
          <button
            className={`toggle-button ${selectedCodeType === 'html' ? 'active' : ''}`}
            onClick={() => handleToggle('html')}
          >
            HTML
          </button>
          <button
            className={`toggle-button ${selectedCodeType === 'css' ? 'active' : ''}`}
            onClick={() => handleToggle('css')}
          >
            CSS
          </button>
          <button
            className={`toggle-button ${selectedCodeType === 'js' ? 'active' : ''}`}
            onClick={() => handleToggle('js')}
          >
            JS
          </button>
        </div>

        <div className="code-display">
          {selectedCodeType === 'html' && <pre>{generatedData.html}</pre>}
          {selectedCodeType === 'css' && <pre>{generatedData.css}</pre>}
          {selectedCodeType === 'js' && <pre>{generatedData.js}</pre>}
        </div>
      </main>
    </div>
  );
}

export default ResultsPage;
