import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Prism from 'prismjs';
import 'prismjs/themes/prism-okaidia.css'; // Import Prism.js theme
import './App.css';

function ResultsPage() {
  const { state } = useLocation();
  const [selectedCodeType, setSelectedCodeType] = useState('html');
  const { generatedData } = state;

  // Apply Prism.js syntax highlighting when the component updates
  useEffect(() => {
    Prism.highlightAll();
  }, [selectedCodeType]);

  const handleToggle = (codeType) => {
    setSelectedCodeType(codeType);
  };

  return (
    <div className="container">
      <header>
        <img src="/logo-no-background.png" alt="Logo" className="logo-top-left" />
      </header>

      <main>
        <div className="toggle-buttons">
          <button
            className={`toggle-button ${selectedCodeType === 'html' ? 'active' : ''}`}
            onClick={() => handleToggle('html')}
          >
            HTML
          </button>
          <button
            className={`toggle-button ${selectedCodeType === 'css' ? 'active' : ''}`}
            onClick={() => handleToggle('css')}
          >
            CSS
          </button>
          <button
            className={`toggle-button ${selectedCodeType === 'js' ? 'active' : ''}`}
            onClick={() => handleToggle('js')}
          >
            JS
          </button>
        </div>

        <div className="code-container">
          {/* HTML code */}
          {selectedCodeType === 'html' && (
            <pre>
              <code className="language-html">{generatedData.html}</code>
            </pre>
          )}

          {/* CSS code */}
          {selectedCodeType === 'css' && (
            <pre>
              <code className="language-css">{generatedData.css}</code>
            </pre>
          )}

          {/* JS code */}
          {selectedCodeType === 'js' && (
            <pre>
              <code className="language-javascript">{generatedData.js}</code>
            </pre>
          )}
        </div>
      </main>
    </div>
  );
}

export default ResultsPage;
