import React, { useState } from 'react';
import './App.css';

function UploadPage() {
  const [inputText, setInputText] = useState('');
  const [result, setResult] = useState(null);
  const [isOverlayOpen, setIsOverlayOpen] = useState(false); // Control overlay visibility
  const [error, setError] = useState(null);
  const [statusMessage, setStatusMessage] = useState(''); // New state to show status messages

  // Handle text input change
  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  // Handle form submission and send input text to backend
  const handleSubmit = async (event) => {
    event.preventDefault();
    setStatusMessage('Input received! Processing...'); // Acknowledge input receipt
    setError(null); // Reset any previous errors
    setResult(null); // Reset the result box

    try {
      const response = await fetch('http://localhost:5001/api/process-text', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ inputText }),
      });

      console.log("INFO: Response status:", response.status);

      if (response.ok) {
        const result = await response.json();
        console.log("INFO: Response from backend:", result);
        if (result.generatedCode) {
          setResult(result.generatedCode); // Set the generated code
          setStatusMessage('Code generated successfully!'); // Show success message
        } else {
          setStatusMessage('Received but no code generated.'); // Acknowledge but no code generated
        }
        setIsOverlayOpen(true); // Open the overlay to display the result
      } else if (response.status === 400) {
        // Specific handling for 400 error (No input text provided)
        setError('No input text provided.');
        setStatusMessage('Please enter a valid prompt.');
      } else {
        const errorMessage = `Error generating code: ${response.status} ${response.statusText}`;
        console.error(errorMessage);
        setError(errorMessage); // Show error message
        setStatusMessage('Error processing the request.'); // Status message for error
      }
    } catch (error) {
      console.error("Error processing request:", error);
      setError('Error processing request');
      setStatusMessage('Error sending the request.'); // Show status for network errors
    }
  };

  return (
    <div className="container">
      <header>
        <img src="/logo-no-background.png" alt="Beerings Logo" className="logo-top-left" />
      </header>

      <main>
        <form onSubmit={handleSubmit}>
          <div className="input-area">
            <textarea
              value={inputText}
              onChange={handleInputChange}
              placeholder="Enter your prompt or text for the model"
              className="text-input"
              rows="5"
            />
            <button type="submit" className="submit-button">Generate Code</button>
          </div>
        </form>

        {/* Display status messages */}
        {statusMessage && <div className="status-message">{statusMessage}</div>}

        {error && <div className="error-message">{error}</div>}

        {/* Full-Screen Overlay */}
        {isOverlayOpen && (
          <div className="overlay">
            <div className="overlay-content">
              <h3>Generated Code:</h3>
              <pre>{result}</pre>
              <button className="close-button" onClick={() => setIsOverlayOpen(false)}>
                Close
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default UploadPage;
