import React, { useState } from 'react';
import './App.css';

function UploadPage() {
  const [inputText, setInputText] = useState('');
  const [result, setResult] = useState(null);
  const [isOverlayOpen, setIsOverlayOpen] = useState(false);
  const [statusMessage, setStatusMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setLoading(true);
    setStatusMessage('Processing...');
    setResult(null);

    // Simulate a "request" delay with setTimeout
    setTimeout(() => {
      // Generate a mock result (simulated "generated code")
      const generatedCode = `<div>Your generated HTML: <strong>${inputText}</strong></div>`;
      setResult(generatedCode);
      setStatusMessage('Code generated successfully!');
      setLoading(false);
      setIsOverlayOpen(true);
    }, 2000); // Simulate 2-second delay
  };

  return (
    <div className="container">
      <header>
        <img src="/logo-no-background.png" alt="Logo" className="logo-top-left" />
      </header>

      <main>
        <form onSubmit={handleSubmit}>
          <div className="input-area">
            <textarea
              value={inputText}
              onChange={handleInputChange}
              placeholder="Enter your prompt or text for the model"
              className="text-input"
              rows="5"
            />
            <button type="submit" className="submit-button" disabled={loading}>
              {loading ? 'Processing...' : 'Generate Code'}
            </button>
          </div>
        </form>

        {/* Display status message */}
        {statusMessage && <div className="status-message">{statusMessage}</div>}

        {/* Show the overlay when the result is available */}
        {isOverlayOpen && (
          <div className="overlay">
            <div className="overlay-content">
              <h3>Generated Code:</h3>
              <pre className="generated-code">{result}</pre>
              <button className="close-button" onClick={() => setIsOverlayOpen(false)}>
                Close
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default UploadPage;
