import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Prism from 'prismjs';
import 'prismjs/themes/prism-okaidia.css'; // Optional theme for code highlighting
import './App.css';

function ResultsPage({ prompt }) {
  const [generatedHTML, setGeneratedHTML] = useState('');
  const [generatedCSS, setGeneratedCSS] = useState('');
  const [generatedJS, setGeneratedJS] = useState('');
  const [selectedCodeType, setSelectedCodeType] = useState('html');
  const [loading, setLoading] = useState(false);

  const apiKey = process.env.meow; // Get the API key from the .env file

  // Function to send the request to OpenAI API
  const fetchGeneratedCode = async (prompt) => {
    setLoading(true);

    try {
      // Sending request to OpenAI API
      const response = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-3.5-turbo',
          messages: [
            { role: 'system', content: 'You are a code generation assistant.' },
            { role: 'user', content: prompt },
          ],
          max_tokens: 1000,
        },
        {
          headers: {
            Authorization: `Bearer ${apiKey}`,
            'Content-Type': 'application/json',
          },
        }
      );

      const generatedText = response.data.choices[0].message.content;

      // Parsing the generated text to separate HTML, CSS, and JS
      const htmlMatch = generatedText.match(/<html>[\s\S]*<\/html>/);
      const cssMatch = generatedText.match(/<style>[\s\S]*<\/style>/);
      const jsMatch = generatedText.match(/<script>[\s\S]*<\/script>/);

      setGeneratedHTML(htmlMatch ? htmlMatch[0] : ''); // Set HTML code
      setGeneratedCSS(cssMatch ? cssMatch[0] : ''); // Set CSS code
      setGeneratedJS(jsMatch ? jsMatch[0] : ''); // Set JS code

      Prism.highlightAll(); // Apply syntax highlighting
    } catch (error) {
      console.error('Error fetching code from OpenAI:', error);
    }

    setLoading(false);
  };

  useEffect(() => {
    // Fetch generated code from OpenAI when the component mounts or prompt changes
    if (prompt) {
      fetchGeneratedCode(prompt);
    }
  }, [prompt]);

  const handleToggle = (codeType) => {
    setSelectedCodeType(codeType);
  };

  return (
    <div className="container">
      <header>
        <img src="/logo-no-background.png" alt="Logo" className="logo-top-left" />
      </header>

      <main>
        {loading ? (
          <div>Loading...</div>
        ) : (
          <>
            <div className="toggle-buttons">
              <button
                className={`toggle-button ${selectedCodeType === 'html' ? 'active' : ''}`}
                onClick={() => handleToggle('html')}
              >
                HTML
              </button>
              <button
                className={`toggle-button ${selectedCodeType === 'css' ? 'active' : ''}`}
                onClick={() => handleToggle('css')}
              >
                CSS
              </button>
              <button
                className={`toggle-button ${selectedCodeType === 'js' ? 'active' : ''}`}
                onClick={() => handleToggle('js')}
              >
                JS
              </button>
            </div>

            <div className="code-container">
              {selectedCodeType === 'html' && (
                <pre>
                  <code className="language-html">{generatedHTML}</code>
                </pre>
              )}
              {selectedCodeType === 'css' && (
                <pre>
                  <code className="language-css">{generatedCSS}</code>
                </pre>
              )}
              {selectedCodeType === 'js' && (
                <pre>
                  <code className="language-javascript">{generatedJS}</code>
                </pre>
              )}
            </div>
          </>
        )}
      </main>
    </div>
  );
}

export default ResultsPage;
