import React, { useState, useEffect } from 'react';
import Prism from 'prismjs';
import 'prismjs/themes/prism-okaidia.css';
import './App.css';

function ResultsPage({ prompt }) {
  const [generatedHTML, setGeneratedHTML] = useState('');
  const [generatedCSS, setGeneratedCSS] = useState('');
  const [generatedJS, setGeneratedJS] = useState('');
  const [selectedCodeType, setSelectedCodeType] = useState('html');

  // Generate code based on the prompt when the component mounts
  useEffect(() => {
    setGeneratedHTML(generateHTML(prompt));
    setGeneratedCSS(generateCSS(prompt));
    setGeneratedJS(generateJS(prompt));
    Prism.highlightAll(); // Apply syntax highlighting after the code is set
  }, [prompt]);

  const handleToggle = (codeType) => {
    setSelectedCodeType(codeType);
  };

  return (
    <div className="container">
      <header>
        <img src="/logo-no-background.png" alt="Logo" className="logo-top-left" />
      </header>

      <main>
        <div className="toggle-buttons">
          <button
            className={`toggle-button ${selectedCodeType === 'html' ? 'active' : ''}`}
            onClick={() => handleToggle('html')}
          >
            HTML
          </button>
          <button
            className={`toggle-button ${selectedCodeType === 'css' ? 'active' : ''}`}
            onClick={() => handleToggle('css')}
          >
            CSS
          </button>
          <button
            className={`toggle-button ${selectedCodeType === 'js' ? 'active' : ''}`}
            onClick={() => handleToggle('js')}
          >
            JS
          </button>
        </div>

        <div className="code-container">
          {selectedCodeType === 'html' && (
            <pre>
              <code className="language-html">{generatedHTML}</code>
            </pre>
          )}
          {selectedCodeType === 'css' && (
            <pre>
              <code className="language-css">{generatedCSS}</code>
            </pre>
          )}
          {selectedCodeType === 'js' && (
            <pre>
              <code className="language-javascript">{generatedJS}</code>
            </pre>
          )}
        </div>
      </main>
    </div>
  );
}

export default ResultsPage;
