import React, { useState } from 'react';
import './App.css';

function UploadPage() {
  const [inputText, setInputText] = useState('');
  const [result, setResult] = useState(null);
  const [isOverlayOpen, setIsOverlayOpen] = useState(false); // Control overlay visibility
  const [error, setError] = useState(null);

  // Handle text input change
  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  // Handle form submission and send input text to backend
  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:5001/api/process-text', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ inputText }),
      });

      if (response.ok) {
        const result = await response.json();
        setResult(result.generatedCode); // Set the result
        setIsOverlayOpen(true); // Open the overlay to display the result
      } else {
        setError('Error generating code');
      }
    } catch (error) {
      setError('Error processing request');
    }
  };

  return (
    <div className="container">
      <header>
        <img src="/logo-no-background.png" alt="Beerings Logo" className="logo-top-left" />
      </header>

      <main>
        <form onSubmit={handleSubmit}>
          <div className="input-area">
            <textarea
              value={inputText}
              onChange={handleInputChange}
              placeholder="Enter your prompt or text for the model"
              className="text-input"
              rows="5"
            />
            <button type="submit" className="submit-button">Generate Code</button>
          </div>
        </form>

        {error && <div className="error-message">{error}</div>}

        {/* Full-Screen Overlay */}
        {isOverlayOpen && (
          <div className="overlay">
            <div className="overlay-content">
              <h3>Generated Code:</h3>
              <pre>{result}</pre>
              <button className="close-button" onClick={() => setIsOverlayOpen(false)}>
                Close
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default UploadPage;
